sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var whatneeded = g_form.getValue('u_what_needed');

   //Clear all the choices from the What needed field choice list
	g_form.clearOptions('u_what_needed');

	//if the value of the request type field is Choice 1, add
	// and two Choices and Other to the what Needed choice list

	if(newValue == 'Choice 1') {

		g_form.addOption('u_what_needed','Choice 1', 'Choice 1.1');
	}
}
